from gmpy2 import (
    mpz,
    isqrt,
    mul,
    sub,
    t_div,
)

N = mpz()

A2 = 2 * isqrt(6 * N) + 1

x2 = isqrt(A2 * A2 - 24 * N)

p = t_div((A2 - x2), 6)
q = t_div((A2 + x2), 4)
print((p, q))
print(p * q == N)
print(p - q)

p = t_div((A2 - x2), 4)
q = t_div((A2 + x2), 6)
print((p, q))
print(p * q == N)
print(p - q)
